sys_script_include.script:
var GetBlueprints = Class.create();
GetBlueprints.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	getBlueprints: function() {
		gs.info("Requesting login");
		var login = new sn_ws.RESTMessageV2();
		login.setEndpoint("https://10.192.0.11/api/aaa/login");
		login.setHttpMethod("post");
		login.setRequestBody('{"username": "admin", "password": "admin"}');
		login.setMIDServer("mid");

		var login_result = JSON.parse(login.execute().getBody());

		gs.info("Requesting blueprints");
		var req = new sn_ws.RESTMessageV2();
		req.setEndpoint("https://10.192.0.11/api/blueprints");
		req.setHttpMethod("get");
		req.setRequestHeader("AuthToken", login_result.token);
		req.setMIDServer("mid");

		var result = req.execute().getBody();

		return(result);
	},

	test: function() {
		gs.info("Hi");
		return("Hi");
	},
	type :'GetBlueprints'
});
